import styled from "styled-components";
import email_svg from "../assets/img/mail.svg";
import password_svg from "../assets/img/password.svg";

const STATUS_SIZES = {
  md: "10px",
  lg: "20px",
};
const FONT_SIZE = {
  sm: "1rem",
  md: "1.5rem",
  lg: "2rem",
};
const WIDTH = {
  sm: "50%",
  md: "70%",
  lg: "100%",
};
const INPUT_ICON = {
  email: email_svg,
  password: password_svg,
};

export const StatusStyled = styled.div`
  width: ${(props) => STATUS_SIZES[props.size] || "1px"};
  height: ${(props) => STATUS_SIZES[props.size] || "1px"};
  border-radius: ${(props) => (props.size === "lg" ? "25%" : "50%")};
  background-color: ${(props) => (props.email ? "green" : "red")};
`;

export const FormErrorMessage = styled.p`
  margin-top: ${(props) => (props.hasError ? "10px" : "0")};
  color: red;
  font-size: 12px;
  font-weight: 100;
`;

export const FormGroupStyle = styled.div`
  width: ${(props) => WIDTH[props.width] || "100%"};
  height: ${(props) =>
    props.width === "sm" ? "40px" : props.width === "md" ? "50px" : "60px"};
  position: relative;
  span {
    position: absolute;
    width: fit-content;
    top: 50%;
    tra
    right: 10px;
    font-size: 20px;
    cursor: pointer;
  }
`;

export const InputStyle = styled.input`
  width: 100%;
  height: 100%;
  background-image: url(${(props) => INPUT_ICON[props.icon] || "none"});
  display: block;
  border: none;
  outline: none;
  border-bottom: 1px solid #bebbbb;
  box-shadow: none;
  padding-left: 35px;
  padding-bottom: 0px;
  padding-top: 5px;
  background-size: 20px;
  background-repeat: no-repeat;
  background-position: 2% 50%;
  margin-top: 15px;
  transition: all linear 0.2s;
  cursor: pointer;
  border-color: #2676ee;
`;

export const ButtonStyle = styled.button`
  width: ${(props) => WIDTH[props.width] || "100%"};
  height: 40px;
  margin-top: 25px;
  border-radius: 20px;
  color: white;
  text-transform: uppercase;
  border: 1px solid transparent;
  outline: none;
  box-shadow: none;
  transition: all linear 0.2s;
  cursor: pointer;
  font-size: 0.8vw;
  font-weight: 600;
  background: #2676ee;
  &:hover {
    border-color: #2676ee;
    color: #2676ee;
    background-color: #ffffff;
  }
`;
