import React from "react";
import { useForm } from "react-hook-form";
import styled from "styled-components";
import { EMAIL_REGEX, PASSWORD_REGEX } from "../../regex";

// Login Background Part
const LoginRightContainer = styled.div`
  display: flex;
  align-items: center;
  justify-content: center;
  width: calc(100% / 3);
  height: 100%;
  background-color: #ffffff;
`;

export const LoginRight = () => {
  const { register, handleSubmit } = useForm();
  const onSubmit = (data) => console.log(data);

  return (
    <LoginRightContainer>
      <form onSubmit={handleSubmit(onSubmit)}>
        {/* <input
          placeholder="Email"
          type="text"
          {...register("email", {
            validate: (value) =>
              EMAIL_REGEX.test(value) ? null : "Email pattern is wrong.",
          })}
        />
        <input />*/}
        <input
        //   placeholder="Passowrd"
        //   type="password"
          {...register("password", {
            validate: (value) =>
              PASSWORD_REGEX.test(value) ? null : "Password pattern is wrong.",
          })}
        />
        <input {...register("firstName", { required: true, maxLength: 20 })} />
        <input {...register("lastName", { pattern: /^[A-Za-z]+$/i })} />
        <input type="number" {...register("age", { min: 18, max: 99 })} />
        <button type="submit">Login</button>
      </form>
    </LoginRightContainer>
  );
};
