import React, { useRef } from "react";
import { useForm } from "react-hook-form";
import styled from "styled-components";
import { InputStyle, ButtonStyle } from "../../components/styled";
import { EMAIL_REGEX, PASSWORD_REGEX } from "../../regex";
import { AiTwotoneEye, AiTwotoneEyeInvisible } from "react-icons/ai";

// Login Background Part
const LoginRightContainer = styled.div`
  width: calc(100% / 3);
  height: 100%;
  background-color: #ffffff;
  padding: 0 50px;
  padding-top: 10vw;
  form {
  }
`;

const LoginRightHeader = styled.h1`
  font-size: 2vw;
  text-transform: uppercase;
  font-weight: 600;
  margin-bottom: 25px;
  color: #2676ee;
`;

const FormGroup = styled.div`
  position: relative;
  span {
    position: absolute;
    width: fit-content;
  }
`;

export const LoginRight = () => {
  const {
    register,
    handleSubmit,
    watch,
    formState: { errors },
  } = useForm();
  const [showPassword, setShowPassword] = React.useState(false);
  const password = useRef({});
  password.current = watch("password", "");
  const onSubmit = (data) => console.log(data);

  return (
    <LoginRightContainer>
      <LoginRightHeader>Login</LoginRightHeader>
      <form onSubmit={handleSubmit(onSubmit)}>
        <FormGroup>
          <InputStyle
            font_size={"sm"}
            width={"sm"}
            icon={"email"}
            placeholder="Email"
            type="text"
            {...register("email", {
              validate: (value) =>
                EMAIL_REGEX.test(value) ? null : "Email pattern is wrong.",
            })}
          />
          {errors.email && <p>{errors.email.message}</p>}
        </FormGroup>
        <FormGroup>
          <InputStyle
            font_size={"md"}
            width={"md"}
            icon={"password"}
            placeholder="Password"
            type={showPassword ? "text" : "password"}
            {...register("password", {
              validate: (value) =>
                PASSWORD_REGEX.test(value)
                  ? null
                  : "Password pattern is wrong.",
            })}
          />
          <span onClick={() => setShowPassword((prev) => !prev)}>
            {showPassword ? <AiTwotoneEyeInvisible /> : <AiTwotoneEye />}
          </span>
          {errors.password && <p>{errors.password.message}</p>}
        </FormGroup>
        <FormGroup>
          <InputStyle
            font_size={"lg"}
            width={"lg"}
            icon={"password"}
            placeholder="Repeat password"
            type={showPassword ? "text" : "password"}
            {...register("password_repeat", {
              validate: (value) =>
                value === password.current || "The passwords do not match",
            })}
          />
          <span onClick={() => setShowPassword((prev) => !prev)}>
            {showPassword ? <AiTwotoneEyeInvisible /> : <AiTwotoneEye />}
          </span>
          {errors.password_repeat && <p>{errors.password_repeat.message}</p>}
        </FormGroup>
        <ButtonStyle type="submit" width={"lg"}>
          Login
        </ButtonStyle>
      </form>
    </LoginRightContainer>
  );
};
