import React, { useRef } from "react";
import { useForm } from "react-hook-form";
import styled from "styled-components";
import { EMAIL_REGEX, PASSWORD_REGEX } from "../../regex";

// Login Background Part
const LoginRightContainer = styled.div`
  display: flex;
  align-items: center;
  justify-content: center;
  width: calc(100% / 3);
  height: 100%;
  background-color: #ffffff;
  padding: 0 
`;

const LoginRightHeader = styled.h1`
  font-size: 2vw;
  text-transform: uppercase;
  font-weight: 600;
  margin-bottom: 25px;
  color: #2676ee;
`;

export const LoginRight = () => {
  const {
    register,
    handleSubmit,
    watch,
    formState: { errors },
  } = useForm();
  const password = useRef({});
  password.current = watch("password", "");
  const onSubmit = (data) => console.log(data);

  return (
    <LoginRightContainer>
      <LoginRightHeader>Login</LoginRightHeader>
      <form onSubmit={handleSubmit(onSubmit)}>
        <input
          placeholder="Email"
          type="text"
          {...register("email", {
            validate: (value) =>
              EMAIL_REGEX.test(value) ? null : "Email pattern is wrong.",
          })}
        />
        {errors.email && <p>{errors.email.message}</p>}
        <input
          placeholder="Password"
          type="password"
          {...register("password", {
            validate: (value) =>
              PASSWORD_REGEX.test(value) ? null : "Password pattern is wrong.",
          })}
        />
        {errors.password && <p>{errors.password.message}</p>}
        <input
          placeholder="Repeat password"
          type="password"
          {...register("password_repeat", {
            validate: (value) =>
              value === password.current || "The passwords do not match",
          })}
        />
        {errors.password_repeat && <p>{errors.password_repeat.message}</p>}
        <button type="submit">Login</button>
      </form>
    </LoginRightContainer>
  );
};
